import React from "react";



function UserDiv() {
  const [playerNames, setPlayerNames] = React.useState(Array(12).fill(''));
  const [courts, setCourts] = React.useState([]);
  const [isCached, setIsCached] = React.useState(false);
  React.useEffect(() => {
    // Load cached teams from localStorage on component mount
    const cachedCourts = localStorage.getItem('cachedCourts');
    if (cachedCourts) {
      setCourts(JSON.parse(cachedCourts));
      setIsCached(true);
    }
  }, []);
  const handlePlayerNameChange = (index, event) => {
    const newPlayerNames = [...playerNames];
    newPlayerNames[index] = event.target.value;
    setPlayerNames(newPlayerNames);
  };
  const shuffleTeams = () => {
    const shuffledCourts = [...courts].map(court => ({
      ...court,
      teamA: court.teamA.sort(() => 0.5 - Math.random()),
      teamB: court.teamB.sort(() => 0.5 - Math.random())
    }));
    setCourts(shuffledCourts);
    localStorage.setItem('cachedCourts', JSON.stringify(shuffledCourts));
  };
  const generateTeams = async () => {
    const filledPlayerNames = playerNames.filter(name => name.trim() !== '');
    if (filledPlayerNames.length !== 12) {
      alert('Please enter 12 player names to generate teams.');
      return;
    }
    const shuffledPlayers = filledPlayerNames.sort(() => 0.5 - Math.random());
    const generatedCourts = [];
    for (let i = 0; i < shuffledPlayers.length; i += 2) {
      generatedCourts.push({
        court: (i / 4) + 1,
        teamA: [shuffledPlayers[i], shuffledPlayers[i + 1]],
        teamB: [shuffledPlayers[i + 2], shuffledPlayers[i + 3]]
      });
    }
    setCourts(generatedCourts);
    setIsCached(true);
    localStorage.setItem('cachedCourts', JSON.stringify(generatedCourts));
  };
  const wipeCachedResults = () => {
    localStorage.removeItem('cachedCourts');
    setCourts([]);
    setIsCached(false);
  };
  return (
    <div className="bg-gradient-to-r from-blue-200 via-blue-300 to-blue-400 min-h-screen flex flex-col items-center justify-center pb-20">
    <div className="p-4 w-9/12 max-w-3xl">
      <div className="grid grid-cols-3 gap-4 mb-4">
        {playerNames.map((name, index) => (
          <input
            key={index}
            type="text"
            placeholder={`Player ${index + 1}`}
            value={name}
            onChange={(event) => handlePlayerNameChange(index, event)}
            className="p-2 border rounded shadow"
          />
        ))}
      </div>
      <button
        onClick={generateTeams}
        className="px-4 py-2 bg-blue-500 text-white rounded shadow hover:bg-blue-700 transition duration-300 mb-4"
      >
        Generate Teams
      </button>
      {isCached && (
        <div className="flex space-x-4 mb-4">
          <button
            onClick={shuffleTeams}
            className="px-4 py-2 bg-green-500 text-white rounded shadow hover:bg-green-700 transition duration-300"
          >
            Shuffle Teams
          </button>
          <button
            onClick={wipeCachedResults}
            className="px-4 py-2 bg-red-500 text-white rounded shadow hover:bg-red-700 transition duration-300"
          >
            Wipe Cached Results
          </button>
        </div>
      )}
      <div className="p-4 w-full max-w-4xl mx-auto">
      <div className="mt-4">
        {courts.length > 0 && (
          <div className="space-y-4">
            {courts.map((court, index) => (
              <div key={index} className="p-4 w-full max-w-4xl mx-auto">
                <h3 className="text-lg font-bold mb-2">Court {court.court}</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <h4 className="text-md font-bold mb-1">Team A</h4>
                    <p>{court.teamA[0]}</p>
                    <p>{court.teamA[1]}</p>
                  </div>
                  <div>
                    <h4 className="text-md font-bold mb-1">Team B</h4>
                    <p>{court.teamB[0]}</p>
                    <p>{court.teamB[1]}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
    </div>
    </div>
  );
}
  export default UserDiv;
